    }
  }
}

$sizes: 'xxs', 'xs', 'sm', 'md', 'lg', 'xl', 'xxl';

@each $size in $sizes {
  .lg-brand-icon--#{$size} {
    width: var(--brand-icon-#{$size});
  }
