@import '../../styles/mixins.scss';

.lg-carousel {
  overflow: hidden;
  position: relative;

  &__button-timer {
    position: absolute;
    width: var(--space-xl);
    height: var(--space-xl);
    transform: rotateY(-180deg) rotateZ(-90deg) rotateX(180deg);

    &__circle {
      stroke-dasharray: calc(7 * var(--space-unit));
      stroke-dashoffset: 0;
      stroke-linecap: round;
      stroke-width: calc(0.125 * var(--space-unit));
      stroke: var(--carousel-active-bullet-color);
      fill: none;
      animation-name: countdown;
      animation-timing-function: linear;
      animation-iteration-count: infinite;
      animation-direction: normal;
    }
  }

  @keyframes countdown {
    from {
      stroke-dashoffset: 7rem;
    }

    to {
      stroke-dashoffset: 0rem;
    }
  }

  &__wrapper {
    position: relative;
    display: flex;
    align-items: stretch;
  }

  &__navigation {
    padding-top: var(--space-sm);
    display: flex;
    justify-content: center;
  }

  &--centered {
    display: flex;
    justify-content: center;
    align-items: center;
  }

  &__button {
    padding: 0;
    border: none;
    background-color: transparent;
    margin: 0;
    width: var(--space-xl);
    height: var(--space-xl);
    cursor: pointer;
    position: relative;

    &:focus-visible {
      outline: none;
    }

    &__disk {
      width: var(--space-xl);
      height: var(--space-xl);
      border-radius: 100%;
    }

    &:hover:not([disabled]) .lg-carousel__button__disk {
      background-color: var(--carousel-nav-background-color);
    }

    &[disabled] {
      cursor: default;
    }
  }

  &__bullet {
    width: var(--space-xxs);
    height: var(--space-xxs);
    background-color: var(--carousel-bullet-color);
    display: block;
    border-radius: 100%;

    &--active {
      width: var(--space-xs);
      height: var(--space-xs);
      background-color: var(--carousel-active-bullet-color);
    }
  }
}

