    color: var(--quick-action-active-color);
  }

  &:visited {
    color: var(--quick-action-visited-color);
  }

  &:focus {
    color: var(--link-color);
  }

  &:focus,
  &:focus:hover {
    @include lg-outer-focus-outline(var(--default-focus-color));

    background-color: var(--quick-action-bg-color);
    color: var(--link-color);
  }

  &:visited:hover {
