import { Directive, ElementRef, Input, HostBinding, OnDestroy } from '@angular/core';

@Directive({
  selector: '[lgSkeleton]',
})
export class LgSkeletonDirective implements OnDestroy {
  private changes: MutationObserver;
  private hasContent = false;

  @Input() lgSkeletonWidth: string = null;
  @Input() lgSkeletonHeight: string = null;
  @Input() lgSkeletonRightAlign = false;

  @HostBinding('class.lg-skeleton')
  get skeletonClass() {
    return !this.hasContent;
  }

  @HostBinding('class.lg-skeleton--right')
  get alignClass() {
    return this.lgSkeletonRightAlign && !this.hasContent;
  }

  @HostBinding('style.width')
  get backgroundWidth(): string {
    if (this.hasContent) {
      return null;
    }

    return this.lgSkeletonWidth ? `${this.lgSkeletonWidth}em` : '100%';
  }

  @HostBinding('style.height')
  get backgroundHeight(): string {
    if (this.hasContent) {
      return null;
    }

    return this.lgSkeletonHeight && !this.hasContent
      ? `${this.lgSkeletonHeight}em`
      : 'auto';
  }

  constructor(private hostElement: ElementRef) {
    this.changes = new MutationObserver(this.observeChanges);

    this.changes.observe(this.hostElement.nativeElement, {
      subtree: true,
      characterData: true,
    });
  }

  observeChanges = (mutations: Array<MutationRecord>) => {
    mutations.forEach((mutation: MutationRecord) => {
      this.hasContent = mutation.target.textContent.trim() !== '';
    });
  };

  ngOnDestroy() {
    this.changes.disconnect();
  }
}

import { Directive, HostBinding, HostListener, OnInit } from '@angular/core';
import { NgControl, Validators } from '@angular/forms';

@Directive({
  selector: '[lgSortCode]',
})
export class LgSortCodeDirective implements OnInit {
  @HostBinding('placeholder') placeholder = '00-00-00';
  @HostBinding('required') required = true;
  @HostBinding('attr.inputmode') inputmode = 'numeric';
  // 8 because we allow for the two dashes
  @HostBinding('attr.maxlength') maxlength = '8';
  @HostBinding('attr.size') size = '7';
  @HostListener('focusout', ['$event.target.value']) onBlur(value) {
    if (this.ngControl.valid) {
      this.ngControl.control.setValue(this.format(value), { emitEvent: false });
    }
  }

  constructor(private ngControl: NgControl) {}

  ngOnInit(): void {
    const validators = [
      Validators.required,
      Validators.pattern(/^(?:\d{6}|\d\d-\d\d-\d\d|\d\d\s\d\d\s\d\d)$/),
    ];

    if (this.ngControl.control.validator) {
      validators.push(this.ngControl.control.validator);
    }
    this.ngControl.control.setValidators(validators);
  }

  private format(value: string): string {
    return value
      .replace(/-|\s/g, '')
      .match(/.{1,2}/g)
      .join('-');
  }
}

import {
  ChangeDetectorRef,
  Directive,
  HostBinding,
  Input,
  OnDestroy,
  OnInit,
} from '@angular/core';

import { Subscription, timer } from 'rxjs';

@Directive({
  selector: `[lgSrAlertMessage]`,
})
export class LgSrAlertMessageDirective implements OnInit, OnDestroy {
  @Input() lgSrAlertMessage: boolean;
  @Input() timer = 8000;
  private subscription: Subscription;

  @HostBinding('class.lg-visually-hidden') class = true;

  @HostBinding('attr.aria-hidden') get ariaHidden() {
    return !this.lgSrAlertMessage;
  }

  @HostBinding('attr.role') get role() {
    if (this.lgSrAlertMessage) {
      return 'alert';
    }
    return null;
  }

  @HostBinding('attr.aria-live') get ariaLive() {
    if (this.lgSrAlertMessage) {
      return 'assertive';
    }
    return null;
  }

  constructor(private cdr: ChangeDetectorRef) {}

  ngOnInit(): void {
    this.subscription = timer(this.timer).subscribe(() => {
      this.lgSrAlertMessage = false;
      this.cdr.detectChanges();
    });
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }
}

