    event.preventDefault();
    this.selectedTabIndex = index;
  }

  constructor(public fb: FormBuilder, private registry: LgIconRegistry) {
    this.registry.registerIcons([
      lgIconHome,
      lgIconAdd,
      lgIconArrowDown,
      lgIconClose,
      lgIconSearch,
      lgIconChevronLeft,
    ]);
