import { Component, OnInit } from '@angular/core';

import { storiesOf } from '@storybook/angular';

import { CanopyModule } from '../canopy.module';
import { notes } from './skeleton.notes';

interface Data {
  title?: string;
  subTitle?: string;
  content?: string;
  datapoint?: {
    label: string;
    value: string;
    prefix?: string;
    suffix?: string;
  };
}

@Component({
  selector: 'async-skeleton-loading-card',
  template: `
    <lg-card>
      <lg-card-header>
